includeIf.script:
jq(function() {
    alert('This patient has no Summary page, you will now be forwarded to the patient dashboard');

    /*
     * queryParameters -> handles the query string parameters
     * queryString -> the query string without the fist '?' character
     * re -> the regular expression
     * m -> holds the string matching the regular expression
     */
    var queryParameters = {}, queryString = location.search.substring(1),
            re = /([^&=]+)=([^&]*)/g, m;

    // Creates a map with the query string parameters
    while (m = re.exec(queryString)) {
        queryParameters[decodeURIComponent(m[1])] = decodeURIComponent(m[2]);
    }

    /*
     * Replace the query portion of the URL.
     * jQuery.param() -> create a serialized representation of an array or
     *     object, suitable for use in a URL query string or Ajax request.
     */
    location.href = window.location.protocol + "//" + window.location.host +
                    '/' + OPENMRS_CONTEXT_PATH + '/coreapps/clinicianfacing/patient.page?patientId='
                    + queryParameters['patientId']; // Causes page to reload
});

htmlform.script:
beforeSubmit.push(function () {
    var submitVal = true;
    var submitValStage = [];
    var message1 = "The next appointment date must be after the current visit date";


    if (getValue('5096.value') != "") {
        setValue('5096.error', '');


        submitValStage.push(dateValidator("5096", "encounterDate", "", message1, "", "less_than", true));
    }

    if (submitValStage.indexOf(false) != -1) {
        submitVal = false;
        jq().toastmessage('showErrorToast', "  There are errors during the validation of the data on the form. Please correct them and submit again");
    }
    return submitVal;
});


function enable_disable_fm(selected_option) {

    var class_name = jq(selected_option).attr("class");

    var length = class_name.length;

    var class_id = parseInt(class_name.substring(length - 1, length))

    var row_id = class_id;

    var disable = true;

    var row_1 = '[class^="FamilyMember"][class*="Children1"]';
    var row_2 = '[class^="FamilyMember"][class*="Children2"]';
    var row_3 = '[class^="FamilyMember"][class*="Children3"]';
    var row_4 = '[class^="FamilyMember"][class*="Children4"]';
    var row_5 = '[class^="FamilyMember"][class*="Children5"]';

    var xx = row_1;
    var selected_value = jq(selected_option).find(":selected").text()


    switch (selected_value) {
        case "P":
        case "Y":
            disable = false;
            break;

    }


    switch (row_id) {
        case 1:
            xx = row_1;
            break;
        case 2:
            xx = row_2;
            break;
        case 3:
            xx = row_3;
            break;
        case 4:
            xx = row_4;
            break;
        case 5:
            xx = row_5;
            break;
    }


    jq(xx).each(function () {
        var group = jq(this);

        if (class_name.indexOf('Children') == -1) {

            group.find("input").attr("disabled", disable);
            group.find('select').attr("disabled", disable);
        }
        else if (class_name.indexOf('GrandChildren') == -1) {
            group.find("input").attr("disabled", disable);
        }
        if (disable) {
            /* fade out the fields that are disabled */
            group.find("input").fadeTo(250, 0.25);
            group.find("select").fadeTo(250, 0.25);
        } else {
            /* remove the fade on the fields */
            group.find("input").fadeTo(250, 1);
            group.find("select").fadeTo(250, 1);
        }

    });

}

