includeIf.script:
            jq(function () {
                alert('This patient has no Summary page, you will now be forwarded to the patient dashboard');

                /*
                 * queryParameters -> handles the query string parameters
                 * queryString -> the query string without the fist '?' character
                 * re -> the regular expression
                 * m -> holds the string matching the regular expression
                 */
                var queryParameters = {}, queryString = location.search.substring(1),
                        re = /([^&=]+)=([^&]*)/g, m;

                // Creates a map with the query string parameters
                while (m = re.exec(queryString)) {
                    queryParameters[decodeURIComponent(m[1])] = decodeURIComponent(m[2]);
                }

                /*
                 * Replace the query portion of the URL.
                 * jQuery.param() -> create a serialized representation of an array or
                 *     object, suitable for use in a URL query string or Ajax request.
                 */
                location.href = window.location.protocol + "//" + window.location.host +
                        '/' + OPENMRS_CONTEXT_PATH + '/coreapps/clinicianfacing/patient.page?patientId='
                        + queryParameters['patientId']; // Causes page to reload
            });

link.script:
	    /*
	    Transfer in Unit-TB Number	Only applicable if Transfer In - done -
	    CPT Start Date	Required/Enabled if patient is on CPT -done
	    ART Start Date	Required/Enabled if patient is on ART -done -
	    ART Number	Required/Enabled if patient is on ART -done-
	    No of children in contact	should always be greater than no of children on IPT -done-
	    INR No.	should only be enabled/Reqired if MUAC Colour Code is red or yellow -done
	    Transfer out to	Required/Enabled if treatment outcome is 'transfer out' -done -
	    MUAC/z-score section	applicable for clients aged 6months and above and below 15 years -done -
	    */

	    if (jQuery) {
	        jq(document).ready(function () {
	            if (jq.browser.msie) {
	                jq(":checkbox").click(function () {
	                    jq(this).change();
	                });
	            }
	        });
	    }

	    jQuery().ready(function(jq){
	    	const 	YELLOW_CONCEPT_ID = 99029,
	    			RED_CONCEPT_ID = 99028,
	    			GREEN_CONCEPT_ID = 99027,
					PBC_CONCEPT_ID = 113491,
					INTENSIVE_CONCEPT_ID = 159794,
					CONTINOUS_CONCEPT_ID = 159795,
					RHZE_CONCEPT_ID = 1675,
					RHZ_CONCEPT_ID = 1678;

	        var $inrNumberField = jq('#inr-number').find('input'),
				$sputumSmearResultsField = getField('sputum-smear-results.value'),
				$sputumSmearDateField = getField('sputum-smear-date.value'),
				$muacQuantitativeField = getField('muac-quantitative.value'),
				$muacQualitativeField = getField('muac-qualitative.value'),
	        	$encounterDateField = jq('#encounter-date').find('input[type="hidden"]');


			/*
			* Disable MUAC for patients below 6 months of age */
			var disableMuacForPatientsBelow6Months = function() {
				var patientDob = new Date('');
				var encounterDate = new Date($encounterDateField.val());
				var patientAgeInMonths = moment(encounterDate).diff(patientDob, 'months', false);

				if (patientAgeInMonths < 6) {
					fieldHelper.disable($muacQualitativeField);
					fieldHelper.disable($muacQuantitativeField);
				} else {
					fieldHelper.enable($muacQualitativeField);
					fieldHelper.enable($muacQuantitativeField);				
				}
			};

	        var muacChange = function() {
	        	var muacQuantitative = getValue('muac-quantitative.value');
	        	var muacQualitative = getValue('muac-qualitative.value');
	        	var $muacQuantitativeErrorField = getField('muac-quantitative.error');
	        	var $muacQualitativeErrorField = getField('muac-qualitative.error');

	            if (muacQualitative == YELLOW_CONCEPT_ID || muacQualitative == RED_CONCEPT_ID) {
	                fieldHelper.enable($inrNumberField);
	            } else {
	                fieldHelper.disable($inrNumberField);
	                $inrNumberField.val('');
	                $inrNumberField.change();
	            }

	        	if (muacQuantitative == '' || muacQualitative =='') {
	        		return;
	        	}

	        	var valid = false;

	        	var validRange = {};

                
                     validRange = [
                 			{color: RED_CONCEPT_ID, lower:0, upper:11.4},
                 			{color: YELLOW_CONCEPT_ID, lower:11.5, upper:12.4},
							{color: GREEN_CONCEPT_ID, lower:12.5, upper:50}
                 			];
                
                
                     validRange = [
                 			{color: RED_CONCEPT_ID, lower:0, upper:13.4},
                 			{color: YELLOW_CONCEPT_ID, lower:13.5, upper:14.4},
							{color: GREEN_CONCEPT_ID, lower:14.5, upper:50}
                 			];
                
                
                     validRange = [
                 			{color: RED_CONCEPT_ID, lower:0, upper:16.5},
                 			{color: YES_CONCEPT_ID, lower:16.5, upper:18.9},
							{color: GREEN_CONCEPT_ID, lower:19, upper:100}
                 			];
                

                
                     validRange = [
                 			{color: RED_CONCEPT_ID, lower:0, upper:18.9},
                 			{color: YELLOW_CONCEPT_ID, lower:19, upper:21.9},
							{color: GREEN_CONCEPT_ID, lower:22, upper:100}
                 			];
                

                var selectedValidRange =  validRange.find(function(o) {
					return o.color == muacQualitative;
                });

                if (muacQuantitative >= selectedValidRange.lower && muacQuantitative <= selectedValidRange.upper) {
					valid = true;
                }

                if (!valid) {
					$muacQuantitativeErrorField.html('The MUAC Qualitative and Quatitative values do not match, please check').show();
					$muacQualitativeErrorField.html('The MUAC Qualitative and Quatitative values do not match, please check').show();
                	$muacQuantitativeField.addClass('error');
                	$muacQualitativeField.addClass('error');
                } else {
                	$muacQuantitativeErrorField.html('').hide();
                	$muacQualitativeErrorField.html('').hide();
                	$muacQuantitativeField.removeClass('error');
                	$muacQualitativeField.removeClass('error');
                }
	        };

	        /**
	        * Validation for the TB Drugs section
	        * This validation check works this way:
	        * For each visible row, If any text box or combo box is non-empty, check if there's any other text box or combobox within the same row that is empty.
	        * If so, validation fails and the elements with missing values are highlighted
	        */
	        var drugsValidated = function () {
	            var $rows,
	                $objectsToValidate;
	            var dirty = false;

	            $rows = jq('table#drugs>tbody tr:visible');
	            $rows.each(function () {
	                $objectsToValidate = jq(this).find('input,select'); //Get all text and combo boxes
	                $objectsToValidate.removeClass('inError');
	                $objectsToValidate.each(function () {
	                    $obj = jq(this);
	                    if ($obj.val() !== '') { //Only run the validatiion check if there's at least one element that is non-empty
	                        $objectsToValidate.each(function () {
	                        	var $errorContainer = jq(this).siblings('span.error');
		                    	if (jq(this).is('input[type="radio"]')) { //Validate Radio Buttons
		                    		var $checkedBoxes = jq(this).parents('span:first').find('input').filter(function() {
		                    			return this.checked;
		                    		});
									if ($checkedBoxes.length == 0) {
										$errorContainer.html('Required').show()
		                                dirty = true;
									} else {
										$errorContainer.html('').hide()
									}
		                    	} else {
		                            if (jq(this).val() === '') {
		                                jq(this).addClass('inError');
		                                $errorContainer.html('Required').show()
		                                dirty = true;
		                            } else {
		                                $errorContainer.html('').hide();
		                            }
		                    	}
	                        });
	                    }
	                });
	            });

	            return !dirty;
	        }

			var diseaseClassification = ;

			/*
			*If Disease classification is PBC then require Sputum smear results (Pre-Rx) 
			*/			
			var sputumSmearResultsValidate = function() {
				$sputumSmearResults = getValue('sputum-smear-results.value');
				if (diseaseClassification == PBC_CONCEPT_ID && $sputumSmearResults == '') {
					getField('sputum-smear-results.error').html('Results required for P-BC').show();
					return false;					
				} else {
					getField('sputum-smear-results.error').html('').hide();
					return true;
				}
				
			};

			var sputumSmearResultsCheck = function() {
				if (diseaseClassification == PBC_CONCEPT_ID) {
					fieldHelper.enable($sputumSmearResultsField);
					fieldHelper.enable($sputumSmearDateField);
				} else {
					fieldHelper.disable($sputumSmearResultsField);
					fieldHelper.disable($sputumSmearDateField);
					setValue('sputum-smear-results.value','');
					setValue('sputum-smear-date.value','');
				}
			};

			var disableSelectOption = function($selectField, ids) {
				if (jQuery.isArray(ids)) {
					$selectField.children('option').each(function() {
						this.disabled = false;
						if (ids.indexOf(parseInt(this.value))) {
							this.disabled = 'disabled';
						}
					});
				}
			}

			$('table#drugs').find('input[type=radio]').click(function() {
				if (this.checked == true) {
					var $drugCombo = $(this).parents('tr:first').find('select:first');
					var idsToDisable = []; 
					if (this.value == INTENSIVE_CONCEPT_ID) {
						idsToDisable.push(RHZ_CONCEPT_ID);
					} else if (this.value == CONTINOUS_CONCEPT_ID) {
						idsToDisable.push(RHZE_CONCEPT_ID);
					}
					disableSelectOption($drugCombo, idsToDisable);
				}
			});
			
	        $muacQuantitativeField.change(muacChange);
	        $muacQualitativeField.change(muacChange);

	        sputumSmearResultsCheck();
			disableMuacForPatientsBelow6Months();

	        beforeSubmit.push(function(){
	            var valid = true;
	            var $errorMessageSpan = jq('span#error-message');

	            if (!drugsValidated()) {
	                valid = false;
	            }

	            if (!sputumSmearResultsValidate()) {
	                valid = false;
	            }

	            if (jq('span.error.field-error:visible').length > 0) {
	            	valid = false;
	            }

	            if (valid) {
	            	$errorMessageSpan.hide();
	            } else {
	            	$errorMessageSpan.show();
	            	jq().toastmessage('showErrorToast', "Form Validation failed. Check for errors");
	            }

	            return valid;
	        });

	    });

	    jQuery(document).ready(function(jq) {
	        //Adding new form
	        var $allRows,
	            $otherRows,
	            $firstRow,
	            $thisRow,
	            $nextRow,
	            $hiddenRows,
	            $lastVisibleRow,
	            $nonEmptyBoxes;

	        var showAddButtonForLastVisibleRow = function() {
	            $lastVisibleRow = $('table#drugs > tbody > tr:visible:last');
	            $lastVisibleRow.find('.add').show('slow');
	        };

	        $allRows = jq('table#drugs > tbody > tr');
	        $firstRow = jq('table#drugs tbody tr:first-of-type');
	        $otherRows = jq('table#drugs tbody tr:not(:first-of-type)');

	        
	            $otherRows.hide(); //Hide all rows apart from first row if adding new form
	        	$firstRow.find('.remove').hide(); //Hide the remove button from the first row
	        

	        
	            //Handle loading of existing obs
	            $allRows.hide();
	        	$nonEmptyBoxes = jq('table#drugs tbody tr input[type=text],table#drugs tbody tr select').filter(function() {
	            	return this.value.length > 0;
		        });
		        
		        if ($nonEmptyBoxes.length > 0 ) {
		            $nonEmptyBoxes.each(function() {
		                $thisRow = jq(this).parents('tr:first');
		                $thisRow.show();
		            });

		            if ($nonEmptyBoxes.length == 1) {
		                $nonEmptyBoxes.parents('tr:first').find('.remove').hide(); //Hide the remove button if there is only one visible row
		            }

		        } else {
		            $firstRow.show();
		        	$firstRow.find('.remove').hide();
		    	}
	        

	        jq('.add').click(function(e) { //Handle add button event
	            e.preventDefault();
	            $thisRow = jq(this).parents('tr:first');
	            $nextRow = $thisRow.next();

	            if ($nextRow.length > 0) {
	                $nextRow.show();
	                $thisRow.find('.add').hide();
	            }
	            showAddButtonForLastVisibleRow();
	        });

		    jq('.remove').click(function(e) { //Handle remove button event
		        e.preventDefault();
		        if (confirm('Are you sure you want to remove this record? All fields for this record will be cleared.') == 1) {
			        $thisRow = jq(this).parents('tr:first');
			        $thisRow.hide('slow', function() {
			        	fieldHelper.clearAllFields($thisRow);
				        showAddButtonForLastVisibleRow();
			        });
			    }
		    });
	    });
